public class Main {	public static void main(String[] args) {				AplicacaoNomes aplicacaoNormal = new AplicacaoNomesNormal();		AplicacaoNomes aplicacaoInvertido = new AplicacaoNomesInvertido();				for (int i = 0; i < args.length; i++) {			if (args[i].indexOf(",") != -1)				aplicacaoInvertido.adicionarNome(args[i]);			else 				aplicacaoNormal.adicionarNome(args[i]);		}		aplicacaoNormal.imprimirNomes();		aplicacaoInvertido.imprimirNomes();	}}